import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View } from 'react-native';
import FontAwesome from "@expo/vector-icons/FontAwesome";
import * as Font from "expo-font"
import { useState, useCallback, useEffect } from 'react';
import {Text} from "@rneui/themed"
import { FlatList, Image } from 'react-native'
import { Button, ButtonGroup, Input } from '@rneui/base';
import { NavigationContainer, useRoute } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import AsyncStorage from '@react-native-async-storage/async-storage';


const Stack = createNativeStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Question">
        <Stack.Screen name="Question" component={QuestionScreen} />
        <Stack.Screen name="Summary" component={SummaryScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  )
}

let questionSet = [
  {
    "prompt": "This is the question...",
  "type": "multiple-choice",
  "choices": [
    "choice 1",
    "choice 2",
    "choice 3",
    "choice 4",
  ],
  "correct": 0
},
  {
    "prompt": "This is another  question...",
  "type": "multiple-answer",
  "choices": [
    "choice 1",
    "choice 2",
    "choice 3",
    "choice 4",
  ],
  "correct": [0,2]
},
  {
  "prompt": "This is the third question...",
  "type": "true-false",
  "choices": [
    "choice 1",
    "choice 2",
  ],
  "correct": 1
},
]

function handleNextButton(selectedAnswer, questionIndex, setSelectedAnswer, navigation, score){
  if (selectedAnswer !== null) {
    if (questionIndex === questionSet.length - 1){
      navigation.navigate("Summary", {score: score})
    }else {
    navigation.navigate("Question", {questionIndex: questionIndex +1})

  }
  setSelectedAnswer(null)
}
}

function restartQuiz({navigation}) {
  navigation.navigate("Question", { questionIndex: 0 });
}


function QuestionScreen({navigation}){
  // const [currentQuestion, setCurrentQuestion] = useState(0)
  const [selectedAnswer, setSelectedAnswer] = useState(null)
  const route = useRoute()
  const questionIndex = route.params?.questionIndex || 0;
  const [score, setScore] = useState(0);

  const handleQuestion = useCallback(
  (index) => {
    const isCorrect = questionSet[questionIndex].correct === index;
    if (isCorrect) {
      setScore((prevScore) => prevScore + 1);
    }
    setSelectedAnswer(index);
    console.log(score)
  },
  [questionIndex, setScore]
);

  function questionFormat(){
    if (questionSet[questionIndex].type === "true-false"){
      return (
        <ButtonGroup
        buttons = {["true", "false"]}
        onPress= {handleQuestion}
        selectedIndex={selectedAnswer}
        testID = "choices"
        ></ButtonGroup>
      )
    } else if (questionSet[questionIndex].type === "multiple-choice"){
      return(
       <ButtonGroup
      style={[styles.container]}
      buttons={questionSet[questionIndex].choices}
      selectedIndex={selectedAnswer}
      onPress= {handleQuestion}
      testID="choices" 
      ></ButtonGroup>
      )
    }else if (questionSet[questionIndex].type === "multiple-answer") {
      return(
        <ButtonGroup
        buttons={questionSet[questionIndex].choices}
        testID="choices"
        onPress= {handleQuestion}
        selectedIndexes={selectedAnswer !== null ? [selectedAnswer] : []}
        selectMultiple="true">

        </ButtonGroup>
      )
    }

  }
 return (
    <View style={[styles.container]}>
      <Text>{questionSet[questionIndex].prompt}</Text>
      {questionFormat()}
      <Button
        title="Next Question"
        onPress={() => handleNextButton(selectedAnswer, questionIndex, setSelectedAnswer, navigation)}
        disabled={selectedAnswer === null}
        testID="next-question"
      />
    </View>
)
}

function SummaryScreen({navigation, route}) {
  const scoreValue = route.params.score
  //show how many questions correct

  return (
    <View style={[styles.container]}>
    <Text>You got {scoreValue} out of {questionSet.length} correct</Text>
    <Button onPress={restartQuiz(navigation)}>Restart</Button>
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: "50px"
  },
  image: {
    flex: 1,
    aspectRatio: 1,
    width: "50%",
    backgroundColor: "blue"
  },
  horizontal: {
    flexDirection: "row",
    justifyContent: "space-around",
    padding: 10,
    fontSize: "50px"
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10
  }
});
