import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View } from 'react-native';
import FontAwesome from "@expo/vector-icons/FontAwesome";
import * as Font from "expo-font"
import { useState, useCallback, useEffect } from 'react';
import {CheckBox, Text} from "@rneui/themed"
import { FlatList, Image } from 'react-native'
import { Button, Input } from '@rneui/base';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import AsyncStorage from '@react-native-async-storage/async-storage';

AsyncStorage.setItem('loginData', 'test');
AsyncStorage.setItem('loginData', 'Test1@');

async function cacheFonts(fonts){
  return fonts.map(async (font) => await Font.loadAsync(font))
}
let initTasks = [
  {description: "Task 1", completed: true, key: 1},
  {description: "Task 2", completed: true, key: 2}
]

const Stack = createNativeStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomePage} />
        <Stack.Screen name="DurationExercise" component={DurationExercise} />
        <Stack.Screen name="IntensityExercise" component={IntensityExercise} />
        <Stack.Screen name="RepetitionExercise" component={RepetitionExercise} />
      </Stack.Navigator>
    </NavigationContainer>
  )
}

function HomePage({navigation}){
return (
  <View style={[styles.container]}>
    <Text>Start Your Workout!</Text>
    <Button>Duration Exercise</Button>
    <Button>Intensity Exercise</Button>
    <Button>Repetition Exercise</Button>
  </View>
)
}

function DurationExercise({navigation}) {
   let myTime = 0
   let currentTimer = 0
   function StopWatch(){
    let [running, setRunning] = useState(false)
    let [timer, setTimer] = useState(0)
    let updateTimer = useCallback(() => {
    if(running) {
      setTimer((timer) => timer+10)
    }
  }, [running])
  useEffect(() => {
    currentTimer = setInterval(updateTimer, 10)
    return () => clearInterval(currentTimer)
  }, [running])
  let startStop = useCallback(() => {
    setRunning(!running)
    clearInterval(currentTimer)
  },[running])
  let mins = (Math.floor((timer/(1000*60)) % 60)).toString().padStart(2, "0")
  let secs = (Math.floor((timer / 1000) %60 )).toString().padStart(2, "0")
  let mills = (timer % 1000).toString().padStart(3, "0")
  return (
  <View style={{width:"100vw", textAlign:"center"}}>
      <Text id="myTime" style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{mins}:{secs}:{mills}</Text>
      <View style={[styles.horizontal]}>
      <Button 
        onClick={startStop}>
        {running ? "Pause" : "Start"}
        </Button>
      <Button 
        onClick={()=> {
        setTimer(0)
      }} >Reset</Button>  
      </View>

  </View>
  )
   }
  const [myTimeNew, setNewTime] = useState(myTime)
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Image 
      src="https://www.freeiconspng.com/thumbs/sports-icon-png/sports-running-icon-2.png" 
    alt="pushup" 
    style={{maxHeight: "20em"}}/>
    <View>
      <StopWatch/>
    </View>
    </View>
    //MAKE BUTTON NAGIVATION.NAGIVATE
  );
}

function IntensityExercise ({navigation}) {
 var myCount = 0
var intense = 0
var effort = 0
var heartRate = 70
var heartCounter = 5
function resetCount (){
    myCount = 0
    setCount(myCount)
  }
  const [myCount2, setCount] = useState(myCount)
  function countUp(){
    myCount++
    console.log(myCount)
    setCount(myCount)
  } 
    const [intense2, setIntense] = useState(intense)

  function intensityIncrease(){
    intense++
    console.log(intense)
    setIntense(intense)

  }
  function resetIntensity (){
    intense = 0
    setIntense(intense)
  }
  const [effort2, setEffort] = useState(effort)
  function effortExerted(){
    if(myCount2 > 0 && intense > 0){
    effort = myCount2 * intense
    setEffort(effort)
    }
  }

  function resetEffort(){
    effort = 0
    setEffort(effort)
  }
    const [heart, setHeart] = useState(heartRate)
 function calculateHeartRate(){
if (myCount2 > 0 && intense2 > 0){
    console.log(heartRate + " This is heartRate before logic")
    heartRate = 100 + (Math.floor(Math.random() * heartCounter)) + myCount2
    console.log(((Math.random) % heartCounter) + " seeing if this is a zero")
    console.log(heartCounter + " this is the heartCounter...is it going up??")
    if (heartRate > 180){
        heartRate = 180
    }
        console.log(heartRate + " This is heartRate after logic")

    setHeart(heartRate)
    }
    heartCounter++

  }
  return (
    //CONVERT DIV to <VIEW>, img to <iMAGE>, button to <Button>, and p to <Text>
    <View className="repetition-body" style={{width:"100vw", textAlign:"center"}}>
      <Text className="title">Advanced Workout</Text>
    <Image 
    src="https://creazilla-store.fra1.digitaloceanspaces.com/cliparts/1795235/running-track-clipart-sm.png" 
    alt="pushup" 
    style={{maxHeight: "20em"}} />
    <View className="counter" style={{margin:"auto"}}>
      <Button style={{"fontSize": "3em"}}onClick={countUp}>Finish Rep</Button>
      <Text style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{myCount2}</Text>
      <Button style={{"fontSize": "3em"}}onClick={resetCount}>Reset Reps</Button>
       <Button style={{"fontSize": "3em"}}onClick={intensityIncrease}>Increase Intensity</Button>
      <Text style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{intense2}</Text>
      <Button style={{"fontSize": "3em"}}onClick={resetIntensity}>Reset Intensity</Button>
    <Button style={{"fontSize": "3em"}}onClick={effortExerted}>Calculate Calories</Button>
        <Text style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{effort2}</Text>
    <Button style={{"fontSize": "3em"}}onClick={resetEffort}>Reset Effort</Button>
    <Button style={{"fontSize": "3em"}}onClick={calculateHeartRate}>calculate Heart Rate</Button>
        <Text style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{heart}</Text>




    </View>
    </View>
  );
}

function RepetitionExercise({navigation}) {
  cacheFonts([FontAwesome.font])
  let [tasks, setTasks] = useState(initTasks)
  let [input, setInput] = useState("")
  let updateTask = (task) => {
    console.log(task)
    task.completed = !task.completed
    setTasks([...tasks])
  }
  let addTask = () => {
    let maxKey=0
    tasks.forEach(task => {
      if (task.key > maxKey) {
        maxKey = task.key
      }
    })
    setTasks([...tasks, {
      description: input,
      completed: false,
      key: maxKey+1
    }])
    setInput("")
  }
  let renderItem = ({item}) => {
    return (
    <View style={[styles.horizontal]}>
    <CheckBox 
    textStyle={item.completed ? {
      textDecorationLine: "line-through",
      textDecorationStyle: "solid"
    } : undefined}
    title={item.description} 
    checked={item.completed} 
    onPress={() => updateTask(item)}/>
    <Button title="Details" onPress={() => navigation.navigate("Details")}></Button>
    </View>
  )}
  return (
    <View style={[styles.container]}>
      <View className="repetition-body" style={{width:"100vw", textAlign:"center"}}>
      <Text className="title">Push Ups</Text>
    <Image 
    src="https://cdn-icons-png.flaticon.com/512/76/76861.png" 
    alt="pushup" 
    style={{maxHeight: "20em"}} />
    <View className="counter" style={{margin:"auto"}}>
      <Button style={{"fontSize": "3em"}}onClick={countUp}>Finish Rep</Button>
      <Text style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{myCount2}</Text>
      <Button style={{"fontSize": "3em"}}onClick={resetCount}>Reset Reps</Button>
    </View>
    </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    flex: 1,
    aspectRatio: 1,
    width: "50%",
    backgroundColor: "blue"
  },
  horizontal: {
    flexDirection: "row",
    justifyContent: "space-around",
    padding: 10,
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10
  }
});
