import { StyleSheet, TouchableOpacity, View } from "react-native";
import * as Font from "expo-font";
import { useState, useCallback, useEffect } from "react";
import { Text } from "@rneui/themed";
import { FlatList, Image, TextInput } from "react-native";
import { Button, ButtonGroup } from "@rneui/base";
import { NavigationContainer, useRoute } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import AsyncStorage from "@react-native-async-storage/async-storage";

const Stack = createNativeStackNavigator();

let favoriteBooks = [
  {
    title: "Babel",
    author: "R.F. Kuang",
    genre: "Fantasy",
    notes: "",
  },
  {
    title: "The Way of Kings",
    author: "Brandon Sanderson",
    genre: "Fantasy",
    notes: "",
  },
  {
    title: "A Little Life",
    author: "Hanya Yanigahara",
    genre: "Fiction",
    notes: "",
  },
  {
    title: "Wisdom of Crowds",
    author: "Joe Abercrombie",
    genre: "Fantasy",
    notes: "",
  },
];

const excitedToReadBooks = [
  {
    title: "Empire of Silence",
    author: "Christopher Ruocchio",
    genre: "Sci-fi",
    notes: "",
  },
  {
    title: "Jade City",
    author: "Fonda Lee",
    genre: "Fantasy",
    notes: "",
  },
  {
    title: "Yellowface",
    author: "R.F Kuang",
    genre: "Fiction",
    notes: "",
  },
];

const worstBooks = [
  {
    title: "Emma",
    author: "Jane Austen",
    genre: "Classics",
    notes: "",
  },
  {
    title: "Fight or Flight",
    author: "Samantha Young",
    genre: "Romance",
    notes: "",
  },
  {
    title: "Hounded",
    author: "Kevin Hearne",
    genre: "Fantasy",
    notes: "",
  },
];

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="CollectionsScreen">
        <Stack.Screen name="CollectionsScreen" component={CollectionsScreen} />
        <Stack.Screen name="BooksScreen" component={BooksScreen} />
        <Stack.Screen name="BookInfoScreen" component={BookInfoScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const collections = [
  {
    title: "Favorite Books",
    data: favoriteBooks,
  },
  {
    title: "Excited to Read",
    data: excitedToReadBooks,
  },
  {
    title: "Worst Books",
    data: worstBooks,
  },
];

//show all collections
function CollectionsScreen({ navigation }) {
  const [newCollectionName, setNewCollectionName] = useState(collections);

  const handlePress = (collection) => {
    navigation.navigate("BooksScreen", collection);
  };

  return (
    <View>
      <FlatList
        data={collections}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => handlePress(item)}>
            <View>
              <Text>{item.title}</Text>
            </View>
          </TouchableOpacity>
        )}
      />
      <View style={[styles.horizontal]}>
        <TextInput
          value={newCollectionName}
          onChangeText={setNewCollectionName}
        />
        <Button onPress={handleAddNewCollection}>Add New Collection</Button>
      </View>
    </View>
  );
}

//books in each collection
function BooksScreen({ navigation, route }) {
  const collection = route.params;
  const [books, setVisibleBooks] = useState([]);
  useEffect(() => {
    // show books based on collection
    switch (collection.title) {
      case "Favorite Books":
        setVisibleBooks(favoriteBooks);
        break;
      case "Excited to Read":
        setVisibleBooks(excitedToReadBooks);
        break;
      case "Worst Books":
        setVisibleBooks(worstBooks);
        break;
      default:
        setVisibleBooks([]);
        break;
    }
  }, [collection]);
  function handlePress(book) {
    navigation.navigate("BookInfoScreen", { book });
  }
  return (
    <View>
      <FlatList
        data={books}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => handlePress(item)}>
            <View>
              <Text>{item.title}</Text>
              <Text>by {item.author}</Text>
            </View>
          </TouchableOpacity>
        )}
      />
    </View>
  );
}

function BookInfoScreen({ navigation, route }) {
  const book = route.params.book;
  const [updatedBooks, setUpdatedBooks] = useState([]);
  const [title, setTitle] = useState(book.title);
  const [author, setAuthor] = useState(book.author);
  const [genre, setGenre] = useState(book.genre);
  const [notes, setNotes] = useState(book.notes);

  function saveChanges() {
    const newBook = { title, author, genre, notes };
    const updatedBooksCopy = [...updatedBooks];
    const bookIndex = updatedBooksCopy.findIndex(
      (b) => b.title === book.title && b.author === book.author
    );
    if (bookIndex >= 0) {
      updatedBooksCopy[bookIndex] = newBook;
    } else {
      updatedBooksCopy.push(newBook);
    }
    setUpdatedBooks(updatedBooksCopy);
    console.log("saved");
    navigation.goBack();
  }

  return (
    <View style={[styles.container]}>
      {/* insert text with title of the book here */}
      <Text>Info for {book.title}</Text>
      <View>
        <View>
          <Text>Title:</Text>
          <TextInput
            placeholder="Title"
            value={title}
            onChangeText={setTitle}
          ></TextInput>
        </View>
        <View>
          <Text>Author:</Text>
          <TextInput
            placeholder="Author"
            value={author}
            onChangeText={setAuthor}
          ></TextInput>
        </View>
        <View>
          <Text>Genre:</Text>
          <TextInput
            placeholder="Genre"
            value={genre}
            onChangeText={setGenre}
          ></TextInput>
        </View>
        <View>
          <Text>Other notes:</Text>
          <TextInput
            placeholder="Personal notes about the book"
            value={notes}
            onChangeText={setNotes}
          ></TextInput>
        </View>
      </View>
      <Button title="Save Changes" onPress={saveChanges}>
        Save Changes
      </Button>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    fontSize: 50,
  },
  image: {
    flex: 1,
    aspectRatio: 1,
    width: "50%",
    backgroundColor: "blue",
  },
  horizontal: {
    flexDirection: "row",
    justifyContent: "space-around",
    padding: 10,
    fontSize: 50,
  },
  button: {
    alignItems: "center",
    backgroundColor: "#DDDDDD",
    padding: 10,
  },
});
